javascript:(function() {
  try {
    // Get page details
    const title = document.title || window.location.hostname;
    const url = window.location.href;
    const mdLink = `[${title}](${url})`;

    // Check for secure context
    if (!window.isSecureContext) {
      alert('This bookmarklet requires a secure context (HTTPS)');
      return;
    }

    // Primary method using Clipboard API
    if (navigator.clipboard) {
      navigator.clipboard.writeText(mdLink)
        .then(() => alert('Copied markdown link to clipboard!'))
        .catch(err => {
          console.error('Clipboard API failed:', err);
          useExecCommandFallback();
        });
    } else {
      useExecCommandFallback();
    }

    // Fallback function
    function useExecCommandFallback() {
      const textarea = document.createElement('textarea');
      textarea.value = mdLink;
      textarea.style.position = 'fixed';
      textarea.style.opacity = '0';
      document.body.appendChild(textarea);
      textarea.focus();
      textarea.select();

      try {
        const successful = document.execCommand('copy');
        if (successful) {
          alert('Copied markdown link to clipboard!');
        } else {
          alert('Unable to copy - please try manual copy or check permissions');
          promptForManualCopy(mdLink);
        }
      } catch (e) {
        alert('Copy operation failed: ' + e);
        promptForManualCopy(mdLink);
      }

      document.body.removeChild(textarea);
    }

    // Final fallback - show text for manual copy
    function promptForManualCopy(text) {
      prompt('Copy this markdown link manually:', text);
    }
  } catch (err) {
    alert('Error: ' + err.message);
  }
})();

