    });

    document.getElementById('ml-close-btn').addEventListener('click', function() {
      document.body.removeChild(debugDiv);
    });

    console.log('Debug interface created');
  } catch (err) {
    console.error('MarkLink error:', err);
    alert('MarkLink error: ' + err.message);
  }
})();

