# migrate-to-scoop.ps1
# Script to migrate apps from choco and winget to scoop where possible

Write-Host "Starting migration to maximize Scoop apps..." -ForegroundColor Green

# Create temp directory for logs
$tempDir = $env:TEMP
$logFile = "$tempDir\scoop-migration-log.txt"
"Migration started at $(Get-Date)" | Out-File $logFile

# 1. Get and process choco apps using proper format
Write-Host "`n=== PHASE 1: Processing Chocolatey apps ===" -ForegroundColor Yellow
Write-Host "Getting list of installed Chocolatey packages..." -ForegroundColor White
$chocoApps = @()
$chocoMigrated = @()

try {
    # Use --limit-output for machine-readable pipe-separated format
    Write-Host "Running: choco list --local-only --limit-output" -ForegroundColor DarkGray
    $chocoRaw = choco list --local-only --limit-output 2>$null
    $chocoRaw | Out-File "$tempDir\choco-apps.txt"

    # Parse pipe-separated output (package|version)
    $chocoApps = ($chocoRaw | Where-Object { $_ -and $_ -match "\|" } | ForEach-Object { 
        ($_ -split "\|")[0] 
    })

    Write-Host "Found $($chocoApps.Count) choco apps: $($chocoApps -join ', ')" -ForegroundColor Green
    "Found $($chocoApps.Count) choco apps" | Tee-Object -Append $logFile
} catch {
    Write-Host "ERROR: Failed to get choco apps: $_" -ForegroundColor Red
    "Error getting choco apps: $_" | Tee-Object -Append $logFile
}

if ($chocoApps.Count -gt 0) {
    Write-Host "`nChecking each choco app for Scoop availability..." -ForegroundColor White
    $current = 0
    foreach ($app in $chocoApps) {
        $current++
        $percent = [math]::Round(($current / $chocoApps.Count) * 100)
        Write-Host "[$current/$($chocoApps.Count)] ($percent%) Processing: $app" -ForegroundColor Magenta

        try {
            # Check if already in scoop using proper command
            Write-Host "  Checking if $app is already in scoop..." -ForegroundColor DarkGray
            $scoopInstalled = scoop list $app 2>$null
            if ($scoopInstalled -and $scoopInstalled -match $app) {
                Write-Host "  ✓ $app already in scoop, skipping..." -ForegroundColor Gray
                continue
            }

            # Check if available in scoop
            Write-Host "  Searching for $app in scoop..." -ForegroundColor DarkGray
            $scoopAvailable = scoop search $app 2>$null
            if ($scoopAvailable -and $scoopAvailable -notmatch "No matches found") {
                Write-Host "  ✓ Found $app in scoop! Starting migration..." -ForegroundColor Green
                Write-Host "  → Uninstalling $app from choco..." -ForegroundColor Yellow

                # Uninstall from choco with proper syntax
                $chocoResult = choco uninstall $app --yes --remove-dependencies 2>&1
                if ($LASTEXITCODE -eq 0) {
                    Write-Host "  ✓ Choco uninstall successful" -ForegroundColor Green
                    Write-Host "  → Installing $app in scoop..." -ForegroundColor Yellow

                    # Install in scoop
                    $scoopResult = scoop install $app 2>&1
                    if ($LASTEXITCODE -eq 0) {
                        $chocoMigrated += $app
                        Write-Host "  ✓ SUCCESS: $app migrated from choco to scoop!" -ForegroundColor Green
                        "$app successfully migrated from choco to scoop" | Tee-Object -Append $logFile
                    } else {
                        Write-Host "  ✗ FAILED: Scoop install failed. Rolling back..." -ForegroundColor Red
                        Write-Host "  Scoop error: $scoopResult" -ForegroundColor DarkRed
                        "Failed to install $app in scoop: $scoopResult" | Tee-Object -Append $logFile
                        # Reinstall in choco if scoop failed
                        Write-Host "  → Reinstalling in choco..." -ForegroundColor Yellow
                        choco install $app --yes 2>$null
                    }
                } else {
                    Write-Host "  ✗ FAILED: Choco uninstall failed" -ForegroundColor Red
                    Write-Host "  Choco error: $chocoResult" -ForegroundColor DarkRed
                    "Failed to uninstall $app from choco: $chocoResult" | Tee-Object -Append $logFile
                }
            } else {
                Write-Host "  - $app not available in scoop, skipping..." -ForegroundColor DarkYellow
            }
        } catch {
            Write-Host "  ✗ ERROR processing $app: $_" -ForegroundColor Red
            "Error processing choco app $app : $_" | Tee-Object -Append $logFile
        }
    }
} else {
    Write-Host "No Chocolatey apps found to process." -ForegroundColor Gray
}

$chocoMigrated | Out-File "$tempDir\choco-migrated-to-scoop.txt"

# 2. Get and process winget apps using export for reliable parsing
Write-Host "`n=== PHASE 2: Processing WinGet apps ===" -ForegroundColor Yellow
Write-Host "Getting list of installed WinGet packages..." -ForegroundColor White
$wingetApps = @()
$wingetMigrated = @()

try {
    # Use winget export to get JSON format for reliable parsing
    $exportFile = "$tempDir\winget-export.json"
    Write-Host "Running: winget export --output $exportFile" -ForegroundColor DarkGray
    winget export --output $exportFile --accept-source-agreements 2>$null

    if (Test-Path $exportFile) {
        Write-Host "✓ WinGet export successful, parsing..." -ForegroundColor Green
        $wingetData = Get-Content $exportFile | ConvertFrom-Json
        $wingetApps = $wingetData.Sources[0].Packages | ForEach-Object { $_.PackageIdentifier }
        Write-Host "Found $($wingetApps.Count) winget apps" -ForegroundColor Green
        "Found $($wingetApps.Count) winget apps" | Tee-Object -Append $logFile
    } else {
        Write-Host "✗ WinGet export failed, trying fallback method..." -ForegroundColor Yellow
    }
} catch {
    Write-Host "✗ ERROR with winget export: $_" -ForegroundColor Red
    "Error getting winget apps: $_" | Tee-Object -Append $logFile
}

# Fallback to parsing winget list if export fails
if ($wingetApps.Count -eq 0) {
    try {
        Write-Host "Using fallback: winget list parsing..." -ForegroundColor Yellow
        Write-Host "Running: winget list --accept-source-agreements" -ForegroundColor DarkGray
        $wingetRaw = winget list --accept-source-agreements 2>$null
        $wingetApps = ($wingetRaw | Select-Object -Skip 3 | Where-Object { 
            $_ -and $_ -notmatch "^-+" -and $_ -notmatch "^\s*$" 
        } | ForEach-Object { 
            # Try to extract package ID (usually first column)
            ($_ -split "\s{2,}")[0].Trim()
        }) | Where-Object { $_ -and $_ -ne "" }
        Write-Host "Found $($wingetApps.Count) winget apps (fallback method)" -ForegroundColor Green
        "Found $($wingetApps.Count) winget apps (fallback method)" | Tee-Object -Append $logFile
    } catch {
        Write-Host "✗ ERROR with winget fallback parsing: $_" -ForegroundColor Red
        "Error with winget fallback parsing: $_" | Tee-Object -Append $logFile
    }
}

if ($wingetApps.Count -gt 0) {
    Write-Host "`nChecking each winget app for Scoop availability..." -ForegroundColor White
    $current = 0
    foreach ($app in $wingetApps) {
        $current++
        $percent = [math]::Round(($current / $wingetApps.Count) * 100)
        Write-Host "[$current/$($wingetApps.Count)] ($percent%) Processing: $app" -ForegroundColor Magenta

        try {
            # Check if already in scoop
            Write-Host "  Checking if $app is already in scoop..." -ForegroundColor DarkGray
            $scoopInstalled = scoop list $app 2>$null
            if ($scoopInstalled -and $scoopInstalled -match $app) {
                Write-Host "  ✓ $app already in scoop, skipping..." -ForegroundColor Gray
                continue
            }

            # Check if available in scoop - try both full package ID and just the name
            $appName = ($app -split "\.")[-1]  # Get last part for common names
            Write-Host "  Searching for $app in scoop..." -ForegroundColor DarkGray
            $scoopAvailable = scoop search $app 2>$null
            if (-not $scoopAvailable -or $scoopAvailable -match "No matches found") {
                Write-Host "  Also trying simplified name: $appName" -ForegroundColor DarkGray
                $scoopAvailable = scoop search $appName 2>$null
            }

            if ($scoopAvailable -and $scoopAvailable -notmatch "No matches found") {
                Write-Host "  ✓ Found in scoop! Starting migration..." -ForegroundColor Green
                Write-Host "  → Uninstalling $app from winget..." -ForegroundColor Yellow

                # Uninstall from winget with proper syntax
                $wingetResult = winget uninstall $app --accept-source-agreements --silent 2>&1
                if ($LASTEXITCODE -eq 0) {
                    Write-Host "  ✓ WinGet uninstall successful" -ForegroundColor Green
                    Write-Host "  → Installing $appName in scoop..." -ForegroundColor Yellow

                    # Install in scoop (try app name first, then full ID)
                    $scoopResult = scoop install $appName 2>&1
                    if ($LASTEXITCODE -eq 0) {
                        $wingetMigrated += $app
                        Write-Host "  ✓ SUCCESS: $app migrated from winget to scoop as $appName!" -ForegroundColor Green
                        "$app successfully migrated from winget to scoop as $appName" | Tee-Object -Append $logFile
                    } else {
                        Write-Host "  ✗ FAILED: Scoop install failed. Rolling back..." -ForegroundColor Red
                        Write-Host "  Scoop error: $scoopResult" -ForegroundColor DarkRed
                        "Failed to install $app in scoop: $scoopResult" | Tee-Object -Append $logFile
                        # Reinstall in winget if scoop failed
                        Write-Host "  → Reinstalling in winget..." -ForegroundColor Yellow
                        winget install $app --accept-source-agreements --silent 2>$null
                    }
                } else {
                    Write-Host "  ✗ FAILED: WinGet uninstall failed" -ForegroundColor Red
                    Write-Host "  WinGet error: $wingetResult" -ForegroundColor DarkRed
                    "Failed to uninstall $app from winget: $wingetResult" | Tee-Object -Append $logFile
                }
            } else {
                Write-Host "  - $app not available in scoop, skipping..." -ForegroundColor DarkYellow
            }
        } catch {
            Write-Host "  ✗ ERROR processing $app: $_" -ForegroundColor Red
            "Error processing winget app $app : $_" | Tee-Object -Append $logFile
        }
    }
} else {
    Write-Host "No WinGet apps found to process." -ForegroundColor Gray
}

