# check-and-remigrate.ps1
# Check existing migration data and fix any issues from the original script

Write-Host "Checking existing migration data and fixing any issues..." -ForegroundColor Green

# Create temp directory for logs
$tempDir = $env:TEMP
$logFile = "$tempDir\remigration-log.txt"
"Remigration check started at $(Get-Date)" | Out-File $logFile

# Helper function to try command with and without elevation
function Invoke-CommandWithElevation {
    param(
        [string]$Command,
        [string]$Arguments,
        [string]$Description
    )

    Write-Host "  → $Description..." -ForegroundColor Yellow

    # Try normal user first
    Write-Host "    Trying as normal user..." -ForegroundColor DarkGray
    $result = & $Command $Arguments.Split(' ') 2>&1

    if ($LASTEXITCODE -eq 0) {
        Write-Host "    ✓ Success as normal user" -ForegroundColor Green
        return @{ Success = $true; Output = $result; Method = "normal" }
    } else {
        Write-Host "    ✗ Failed as normal user, trying with elevation..." -ForegroundColor Yellow

        # Try with elevation (sudo/gsudo if available, otherwise Start-Process with RunAs)
        $elevatedResult = $null
        $elevatedSuccess = $false

        # Check if gsudo is available (better than runas for this purpose)
        if (Get-Command gsudo -ErrorAction SilentlyContinue) {
            Write-Host "    Using gsudo for elevation..." -ForegroundColor DarkGray
            $elevatedResult = gsudo $Command $Arguments.Split(' ') 2>&1
            $elevatedSuccess = $LASTEXITCODE -eq 0
        } elseif (Get-Command sudo -ErrorAction SilentlyContinue) {
            Write-Host "    Using sudo for elevation..." -ForegroundColor DarkGray
            $elevatedResult = sudo $Command $Arguments.Split(' ') 2>&1
            $elevatedSuccess = $LASTEXITCODE -eq 0
        } else {
            Write-Host "    No elevation tool found (gsudo/sudo), trying PowerShell RunAs..." -ForegroundColor DarkGray
            try {
                $psi = New-Object System.Diagnostics.ProcessStartInfo
                $psi.FileName = $Command
                $psi.Arguments = $Arguments
                $psi.UseShellExecute = $true
                $psi.Verb = "RunAs"
                $psi.RedirectStandardOutput = $false
                $psi.RedirectStandardError = $false
                $process = [System.Diagnostics.Process]::Start($psi)
                $process.WaitForExit()
                $elevatedSuccess = $process.ExitCode -eq 0
                $elevatedResult = "Process completed with exit code: $($process.ExitCode)"
            } catch {
                $elevatedResult = "Elevation failed: $_"
                $elevatedSuccess = $false
            }
        }

        if ($elevatedSuccess) {
            Write-Host "    ✓ Success with elevation" -ForegroundColor Green
            return @{ Success = $true; Output = $elevatedResult; Method = "elevated" }
        } else {
            Write-Host "    ✗ Failed even with elevation" -ForegroundColor Red
            return @{ Success = $false; Output = $elevatedResult; Method = "failed" }
        }
    }
}

Write-Host "`n=== CHECKING EXISTING MIGRATION DATA ===" -ForegroundColor Yellow

# Check for existing migration files
$chocoAppsFile = "$tempDir\choco-apps.txt"
$chocoMigratedFile = "$tempDir\choco-migrated-to-scoop.txt"
$wingetMigratedFile = "$tempDir\winget-migrated-to-scoop.txt"
$wingetExportFile = "$tempDir\winget-export.json"

$foundExistingData = $false

if (Test-Path $chocoAppsFile) {
    Write-Host "✓ Found existing choco apps file: $chocoAppsFile" -ForegroundColor Green
    $foundExistingData = $true
}

if (Test-Path $chocoMigratedFile) {
    Write-Host "✓ Found existing choco migration file: $chocoMigratedFile" -ForegroundColor Green
    $foundExistingData = $true
}

if (Test-Path $wingetMigratedFile) {
    Write-Host "✓ Found existing winget migration file: $wingetMigratedFile" -ForegroundColor Green
    $foundExistingData = $true
}

if (Test-Path $wingetExportFile) {
    Write-Host "✓ Found existing winget export file: $wingetExportFile" -ForegroundColor Green
    $foundExistingData = $true
}

if (-not $foundExistingData) {
    Write-Host "No existing migration data found. Running fresh migration..." -ForegroundColor Yellow
    Write-Host "Please run the main migrate-to-scoop.ps1 script instead." -ForegroundColor White
    exit
}

# Read previously migrated apps
$previouslyMigratedChoco = @()
$previouslyMigratedWinget = @()

if (Test-Path $chocoMigratedFile) {
    $previouslyMigratedChoco = Get-Content $chocoMigratedFile | Where-Object { $_ -and $_ -ne "" }
    Write-Host "Previously migrated from choco: $($previouslyMigratedChoco.Count) apps" -ForegroundColor Cyan
    if ($previouslyMigratedChoco.Count -gt 0) {
        Write-Host "  → $($previouslyMigratedChoco -join ', ')" -ForegroundColor Gray
    }
}

if (Test-Path $wingetMigratedFile) {
    $previouslyMigratedWinget = Get-Content $wingetMigratedFile | Where-Object { $_ -and $_ -ne "" }
    Write-Host "Previously migrated from winget: $($previouslyMigratedWinget.Count) apps" -ForegroundColor Cyan
    if ($previouslyMigratedWinget.Count -gt 0) {
        Write-Host "  → $($previouslyMigratedWinget -join ', ')" -ForegroundColor Gray
    }
}

Write-Host "`n=== VERIFICATION AND REMEDIATION ===" -ForegroundColor Yellow

# Check current state and fix issues
$fixedApps = @()
$issuesFound = @()

# Check if previously migrated choco apps are actually in scoop
Write-Host "`nChecking choco migrations..." -ForegroundColor White
foreach ($app in $previouslyMigratedChoco) {
    Write-Host "Checking $app..." -ForegroundColor Magenta

    # Check if it's actually in scoop
    $scoopInstalled = scoop list $app 2>$null
    if ($scoopInstalled -and $scoopInstalled -match $app) {
        Write-Host "  ✓ $app correctly in scoop" -ForegroundColor Green

        # Check if it's still in choco (shouldn't be)
        $chocoStillInstalled = choco list --local-only $app 2>$null
        if ($chocoStillInstalled -and $chocoStillInstalled -match $app) {
            Write-Host "  ⚠ WARNING: $app still in both choco and scoop! Removing from choco..." -ForegroundColor Yellow
            $uninstallResult = Invoke-CommandWithElevation -Command "choco" -Arguments "uninstall $app --yes --remove-dependencies" -Description "Removing duplicate $app from choco"
            if ($uninstallResult.Success) {
                Write-Host "  ✓ Removed duplicate from choco" -ForegroundColor Green
                $fixedApps += "$app (removed choco duplicate)"
