Write-Host "="*60 -ForegroundColor Green

if ($fixedApps.Count -gt 0) {
    Write-Host "Apps fixed: $($fixedApps.Count)" -ForegroundColor Green
    foreach ($fix in $fixedApps) {
        Write-Host "  ✓ $fix" -ForegroundColor Cyan
