# Helper function to try command with and without elevation
function Invoke-CommandWithElevation {
    param(
        [string]$Command,
        [string]$Arguments,
        [string]$Description
    )

    Write-Host "  → $Description..." -ForegroundColor Yellow

    # Try normal user first
    Write-Host "    Trying as normal user..." -ForegroundColor DarkGray
    $argArray = $Arguments.Split(' ')
    $result = & $Command @argArray 2>&1

    if ($LASTEXITCODE -eq 0) {
        Write-Host "    ✓ Success as normal user" -ForegroundColor Green
        return @{ Success = $true; Output = $result; Method = "normal" }
    } else {
        Write-Host "    ✗ Failed as normal user, trying with elevation..." -ForegroundColor Yellow

        # Try with elevation (sudo/gsudo if available, otherwise Start-Process with RunAs)
        $elevatedResult = $null
        $elevatedSuccess = $false

        # Check if gsudo is available (better than runas for this purpose)
        if (Get-Command gsudo -ErrorAction SilentlyContinue) {
            Write-Host "    Using gsudo for elevation..." -ForegroundColor DarkGray
            $elevatedResult = gsudo $Command @argArray 2>&1
            $elevatedSuccess = $LASTEXITCODE -eq 0
        } elseif (Get-Command sudo -ErrorAction SilentlyContinue) {
            Write-Host "    Using sudo for elevation..." -ForegroundColor DarkGray
            $elevatedResult = sudo $Command @argArray 2>&1
            $elevatedSuccess = $LASTEXITCODE -eq 0
        } else {
            Write-Host "    No elevation tool found (gsudo/sudo), trying PowerShell RunAs..." -ForegroundColor DarkGray
            try {
                $psi = New-Object System.Diagnostics.ProcessStartInfo
                $psi.FileName = $Command
                $psi.Arguments = $Arguments
                $psi.UseShellExecute = $true
                $psi.Verb = "RunAs"
                $psi.RedirectStandardOutput = $false
                $psi.RedirectStandardError = $false
                $process = [System.Diagnostics.Process]::Start($psi)
                $process.WaitForExit()
                $elevatedSuccess = $process.ExitCode -eq 0
                $elevatedResult = "Process completed with exit code: $($process.ExitCode)"
            } catch {
                $elevatedResult = "Elevation failed: $_"
                $elevatedSuccess = $false
            }
        }

        if ($elevatedSuccess) {
            Write-Host "    ✓ Success with elevation" -ForegroundColor Green
            return @{ Success = $true; Output = $elevatedResult; Method = "elevated" }
        } else {
            Write-Host "    ✗ Failed even with elevation" -ForegroundColor Red
            return @{ Success = $false; Output = $elevatedResult; Method = "failed" }
        }
    }
}

# 1. Get and process choco apps using proper format
Write-Host "`n=== PHASE 1: Processing Chocolatey apps ===" -ForegroundColor Yellow
Write-Host "Getting list of installed Chocolatey packages..." -ForegroundColor White
$chocoApps = @()
$chocoMigrated = @()

try {
    # Use --limit-output for machine-readable pipe-separated format
    Write-Host "Running: choco list --local-only --limit-output" -ForegroundColor DarkGray
    $chocoRaw = choco list --local-only --limit-output 2>$null
    $chocoRaw | Out-File "$tempDir\choco-apps.txt"

    # Parse pipe-separated output (package|version)
    $chocoApps = ($chocoRaw | Where-Object { $_ -and $_ -match "\|" } | ForEach-Object { 
        ($_ -split "\|")[0] 
    })

    Write-Host "Found $($chocoApps.Count) choco apps: $($chocoApps -join ', ')" -ForegroundColor Green
    "Found $($chocoApps.Count) choco apps" | Tee-Object -Append $logFile
} catch {
    Write-Host "ERROR: Failed to get choco apps: $_" -ForegroundColor Red
    "Error getting choco apps: $_" | Tee-Object -Append $logFile
}

if ($chocoApps.Count -gt 0) {
    Write-Host "`nChecking each choco app for Scoop availability..." -ForegroundColor White
    $current = 0
    foreach ($app in $chocoApps) {
        $current++
        $percent = [math]::Round(($current / $chocoApps.Count) * 100)
        Write-Host "[$current/$($chocoApps.Count)] ($percent%) Processing: $app" -ForegroundColor Magenta

        try {
            # Check if available in scoop first
            Write-Host "  Searching for $app in scoop..." -ForegroundColor DarkGray
            $scoopAvailable = scoop search $app 2>$null
            if ($scoopAvailable -and $scoopAvailable -notmatch "No matches found") {
                Write-Host "  ✓ Found $app in scoop!" -ForegroundColor Green

                # Check if already in scoop
                Write-Host "  Checking if $app is already in scoop..." -ForegroundColor DarkGray
                $scoopInstalled = scoop list $app 2>$null
                if ($scoopInstalled -and $scoopInstalled -match $app) {
                    Write-Host "  ✓ $app already in scoop, removing from choco only..." -ForegroundColor Gray
                    # Just uninstall from choco since it's already in scoop
                    $uninstallResult = Invoke-CommandWithElevation -Command "choco" -Arguments "uninstall $app --yes --remove-dependencies" -Description "Removing $app from choco (already in scoop)"
                    if ($uninstallResult.Success) {
                        Write-Host "  ✓ Removed duplicate from choco" -ForegroundColor Green
                        "$app removed from choco (already in scoop)" | Tee-Object -Append $logFile
                    }
                    continue
                }

                # Uninstall from choco first
                $uninstallResult = Invoke-CommandWithElevation -Command "choco" -Arguments "uninstall $app --yes --remove-dependencies" -Description "Uninstalling $app from choco"

                if ($uninstallResult.Success) {
                    Write-Host "  ✓ Choco uninstall successful ($($uninstallResult.Method))" -ForegroundColor Green
                    Write-Host "  → Installing $app in scoop..." -ForegroundColor Yellow

                    # Install in scoop
                    $scoopResult = scoop install $app 2>&1
                    if ($LASTEXITCODE -eq 0) {
                        $chocoMigrated += $app
                        Write-Host "  ✓ SUCCESS: $app migrated from choco to scoop!" -ForegroundColor Green
                        "$app successfully migrated from choco to scoop" | Tee-Object -Append $logFile
                    } else {
                        Write-Host "  ✗ FAILED: Scoop install failed. Rolling back..." -ForegroundColor Red
                        Write-Host "  Scoop error: $scoopResult" -ForegroundColor DarkRed
                        "Failed to install $app in scoop: $scoopResult" | Tee-Object -Append $logFile
                        # Reinstall in choco if scoop failed
                        Write-Host "  → Reinstalling in choco..." -ForegroundColor Yellow
                        $rollbackResult = Invoke-CommandWithElevation -Command "choco" -Arguments "install $app --yes" -Description "Rolling back $app to choco"
                        if ($rollbackResult.Success) {
                            Write-Host "  ✓ Rollback successful" -ForegroundColor Green
                        } else {
                            Write-Host "  ✗ Rollback failed: $($rollbackResult.Output)" -ForegroundColor Red
                        }
                    }
                } else {
                    Write-Host "  ✗ FAILED: Choco uninstall failed ($($uninstallResult.Method))" -ForegroundColor Red
                    Write-Host "  Choco error: $($uninstallResult.Output)" -ForegroundColor DarkRed
                    "Failed to uninstall $app from choco: $($uninstallResult.Output)" | Tee-Object -Append $logFile
                }
            } else {
                Write-Host "  - $app not available in scoop, skipping..." -ForegroundColor DarkYellow
            }
        } catch {
            Write-Host "  ✗ ERROR processing ${app}: $_" -ForegroundColor Red
            "Error processing choco app ${app} : $_" | Tee-Object -Append $logFile
        }
    }
} else {
    Write-Host "No Chocolatey apps found to process." -ForegroundColor Gray
}

$chocoMigrated | Out-File "$tempDir\choco-migrated-to-scoop.txt"

