                    Write-Host "  ✓ $targetScoopName already in scoop, removing from winget only..." -ForegroundColor Gray
                    # Just uninstall from winget since it's already in scoop (use --all-versions)
                    $uninstallResult = Invoke-CommandWithElevation -Command "winget" -Arguments "uninstall $app --accept-source-agreements --silent --all-versions" -Description "Removing $app from winget (already in scoop)"
                    if ($uninstallResult.Success) {
                        Write-Host "  ✓ Removed duplicate from winget" -ForegroundColor Green
                        "$app removed from winget (already in scoop as $targetScoopName)" | Tee-Object -Append $logFile
                    }
                    continue
                }

                # Uninstall from winget first (use --all-versions to handle multiple versions)
                $uninstallResult = Invoke-CommandWithElevation -Command "winget" -Arguments "uninstall $app --accept-source-agreements --silent --all-versions" -Description "Uninstalling $app from winget"

                if ($uninstallResult.Success) {
                    Write-Host "  ✓ WinGet uninstall successful ($($uninstallResult.Method))" -ForegroundColor Green
                    Write-Host "  → Installing $targetScoopName in scoop..." -ForegroundColor Yellow

                    # Install in scoop (try determined name)
                    $scoopResult = scoop install $targetScoopName 2>&1
                    if ($LASTEXITCODE -eq 0) {
                        $wingetMigrated += $app
                        Write-Host "  ✓ SUCCESS: $app migrated from winget to scoop as $targetScoopName!" -ForegroundColor Green
                        "$app successfully migrated from winget to scoop as $targetScoopName" | Tee-Object -Append $logFile
                    } else {
                        Write-Host "  ✗ FAILED: Scoop install failed. Rolling back..." -ForegroundColor Red
                        Write-Host "  Scoop error: $scoopResult" -ForegroundColor DarkRed
                        "Failed to install $app in scoop: $scoopResult" | Tee-Object -Append $logFile
                        # Reinstall in winget if scoop failed
                        Write-Host "  → Reinstalling in winget..." -ForegroundColor Yellow
                        $rollbackResult = Invoke-CommandWithElevation -Command "winget" -Arguments "install $app --accept-source-agreements --silent" -Description "Rolling back $app to winget"
                        if ($rollbackResult.Success) {
                            Write-Host "  ✓ Rollback successful" -ForegroundColor Green
                        } else {
                            Write-Host "  ✗ Rollback failed: $($rollbackResult.Output)" -ForegroundColor Red
                        }
                    }
                } else {
                    Write-Host "  ✗ FAILED: WinGet uninstall failed ($($uninstallResult.Method))" -ForegroundColor Red
                    Write-Host "  WinGet error: $($uninstallResult.Output)" -ForegroundColor DarkRed
                    "Failed to uninstall $app from winget: $($uninstallResult.Output)" | Tee-Object -Append $logFile
                }
            } else {
                $triedNames = if ($namesToTry.Count -gt 0) { " (tried: $($namesToTry -join ', '))" } else { "" }
                Write-Host "  - $app not available in scoop$triedNames, skipping..." -ForegroundColor DarkYellow
            }
        } catch {
            Write-Host "  ✗ ERROR processing ${app}: $_" -ForegroundColor Red
            "Error processing winget app ${app} : $_" | Tee-Object -Append $logFile
        }
    }
} else {
    Write-Host "No WinGet apps found to process." -ForegroundColor Gray
}

