            Write-Host "  - Skipping $app (Adobe Reader often has uninstall issues)" -ForegroundColor Yellow
            continue
        }
        # Skip Microsoft apps - these are best left managed by WinGet
        if ($app -match "^Microsoft\." -and $app -match "(VisualStudio|Office|Teams|Edge|OneDrive|Defender|Store|Xbox)") {
            Write-Host "  - Skipping $app (Microsoft app, best managed by WinGet)" -ForegroundColor Gray
            continue
        }
        # Skip generic utility apps and versioned variants
        if ($app -match "(Updater|Update|Redistributable|Runtime|Helper|Service|Driver|Plugin)$") {
            Write-Host "  - Skipping $app (generic utility/updater)" -ForegroundColor Gray
            continue
