# migrate-to-scoop.ps1
# Script to migrate apps from choco and winget to scoop where possible

Write-Host "Starting migration to maximize Scoop apps..." -ForegroundColor Green

# Create temp directory for logs
$tempDir = $env:TEMP
$logFile = "$tempDir\scoop-migration-log.txt"
"Migration started at $(Get-Date)" | Out-File $logFile

# Helper function to try command with and without elevation
function Invoke-CommandWithElevation {
    param(
        [string]$Command,
        [string]$Arguments,
        [string]$Description
    )

    Write-Host "  → $Description..." -ForegroundColor Yellow

    # Try normal user first
    Write-Host "    Trying as normal user..." -ForegroundColor DarkGray
    $argArray = $Arguments.Split(' ')
    $result = & $Command @argArray 2>&1

    if ($LASTEXITCODE -eq 0) {
        Write-Host "    ✓ Success as normal user" -ForegroundColor Green
        return @{ Success = $true; Output = $result; Method = "normal" }
    } else {
        Write-Host "    ✗ Failed as normal user, trying with elevation..." -ForegroundColor Yellow

        # Try with elevation (sudo/gsudo if available, otherwise Start-Process with RunAs)
        $elevatedResult = $null
        $elevatedSuccess = $false

        # Check if gsudo is available (better than runas for this purpose)
        if (Get-Command gsudo -ErrorAction SilentlyContinue) {
            Write-Host "    Using gsudo for elevation..." -ForegroundColor DarkGray
            $elevatedResult = gsudo $Command @argArray 2>&1
            $elevatedSuccess = $LASTEXITCODE -eq 0
        } elseif (Get-Command sudo -ErrorAction SilentlyContinue) {
            Write-Host "    Using sudo for elevation..." -ForegroundColor DarkGray
            $elevatedResult = sudo $Command @argArray 2>&1
            $elevatedSuccess = $LASTEXITCODE -eq 0
        } else {
            Write-Host "    No elevation tool found (gsudo/sudo), trying PowerShell RunAs..." -ForegroundColor DarkGray
            try {
                $psi = New-Object System.Diagnostics.ProcessStartInfo
                $psi.FileName = $Command
                $psi.Arguments = $Arguments
                $psi.UseShellExecute = $true
                $psi.Verb = "RunAs"
                $psi.RedirectStandardOutput = $false
                $psi.RedirectStandardError = $false
                $process = [System.Diagnostics.Process]::Start($psi)
                $process.WaitForExit()
                $elevatedSuccess = $process.ExitCode -eq 0
                $elevatedResult = "Process completed with exit code: $($process.ExitCode)"
            } catch {
                $elevatedResult = "Elevation failed: $_"
                $elevatedSuccess = $false
            }
        }

        if ($elevatedSuccess) {
            Write-Host "    ✓ Success with elevation" -ForegroundColor Green
            return @{ Success = $true; Output = $elevatedResult; Method = "elevated" }
        } else {
            Write-Host "    ✗ Failed even with elevation" -ForegroundColor Red
            return @{ Success = $false; Output = $elevatedResult; Method = "failed" }
        }
    }
}

# 1. Get and process choco apps using proper format
Write-Host "`n=== PHASE 1: Processing Chocolatey apps ===" -ForegroundColor Yellow
Write-Host "Getting list of installed Chocolatey packages..." -ForegroundColor White
$chocoApps = @()
