        # Skip package managers and problematic apps
        if ($app -match "(winget|Microsoft\.WinGet|scoop)" -or $app -match "ScoopInstaller") {
            Write-Host "  - Skipping $app (package manager)" -ForegroundColor Gray
            continue
        }
        if ($app -match "(PowerShell|pwsh)" -and $PSVersionTable.PSVersion) {
            Write-Host "  - Skipping $app (can't uninstall PowerShell from PowerShell)" -ForegroundColor Gray
            continue
        }
        if ($app -match "(Adobe.*Reader|Acrobat.*Reader)" -and $app -match "Reader") {
            Write-Host "  - Skipping $app (Adobe Reader often has uninstall issues)" -ForegroundColor Yellow
            continue
        }
        # Skip Microsoft apps - these are best left managed by WinGet
        if ($app -match "^Microsoft\." -and $app -match "(VisualStudio|Office|Teams|Edge|OneDrive|Defender|Store|Xbox)") {
            Write-Host "  - Skipping $app (Microsoft app, best managed by WinGet)" -ForegroundColor Gray
            continue
        }
        # Skip generic utility apps and versioned variants
        if ($app -match "(Updater|Update|Redistributable|Runtime|Helper|Service|Driver|Plugin|Launcher)$") {
            Write-Host "  - Skipping $app (generic utility/updater)" -ForegroundColor Gray
            continue
        }

        try {
            # Check if available in scoop - try multiple name variations
            Write-Host "  Searching for $app in scoop..." -ForegroundColor DarkGray
            $scoopAvailable = scoop search $app 2>$null
            $targetScoopName = $app

            if ($scoopAvailable -match "GitHub API rate limit reached") {
                Write-Host "  ⚠ GitHub API rate limit reached, skipping $app for now..." -ForegroundColor Yellow
                Write-Host "    Configure GitHub token with: scoop config gh_token <token>" -ForegroundColor Gray
                continue
            }

            # Try various name extractions if not found
            if (-not $scoopAvailable -or $scoopAvailable -match "No matches found") {
                $namesToTry = @()

                # First try the full app name without version numbers
                $baseAppName = $app -replace "\.(v?\d+(\.\d+)*|20\d\d|LTS)$", "" -replace "\d+\.\d+$", ""
                if ($baseAppName -ne $app) {
                    $namesToTry += $baseAppName
                }

                # Extract meaningful parts from winget package ID
                $parts = $app -split "\."

                # Common patterns for winget package names
                if ($parts.Length -ge 2) {
                    # Try the actual product name (usually last part, but not if it's version/locale)
                    $lastName = $parts[-1]
                    # Strip version numbers from the last part too
                    $lastNameClean = $lastName -replace "(v?\d+(\.\d+)*|20\d\d|LTS)$", "" -replace "\d+\.\d+$", ""
                    if ($lastNameClean -and $lastNameClean -notmatch "^\d" -and $lastNameClean -notmatch "^(en-|x64|64-bit|32-bit)") {
                        $namesToTry += $lastNameClean
                    }

                    # Try second-to-last part (often the product name)
                    if ($parts.Length -ge 3) {
                        $secondLast = $parts[-2]
                        $secondLastClean = $secondLast -replace "(v?\d+(\.\d+)*|20\d\d|LTS)$", "" -replace "\d+\.\d+$", ""
                        if ($secondLastClean -and $secondLastClean -notmatch "^\d" -and $secondLastClean -notmatch "^(en-|x64|64-bit|32-bit)") {
                            $namesToTry += $secondLastClean
                        }
                    }

                    # Try combinations for compound names (also strip versions)
                    if ($parts.Length -ge 3) {
                        $part1Clean = $parts[-2] -replace "(v?\d+(\.\d+)*|20\d\d|LTS)$", "" -replace "\d+\.\d+$", ""
                        $part2Clean = $parts[-1] -replace "(v?\d+(\.\d+)*|20\d\d|LTS)$", "" -replace "\d+\.\d+$", ""
                        if ($part1Clean -and $part2Clean -and $part1Clean -notmatch "^\d" -and $part2Clean -notmatch "^\d") {
                            $combined = $part1Clean + $part2Clean
                            if ($combined -notmatch "^(en-|x64|64-bit|32-bit)") {
                                $namesToTry += $combined
                            }
                        }
                    }
                }

                # Try searching with each name variation
                foreach ($testName in $namesToTry) {
                    if ($testName -and $testName.Length -gt 2) {  # Skip very short names
                        Write-Host "    Trying name variation: $testName" -ForegroundColor DarkGray
                        $scoopAvailable = scoop search $testName 2>$null

                        if ($scoopAvailable -match "GitHub API rate limit reached") {
                            Write-Host "  ⚠ GitHub API rate limit reached, skipping $app for now..." -ForegroundColor Yellow
                            Write-Host "    Configure GitHub token with: scoop config gh_token <token>" -ForegroundColor Gray
                            continue
                        }

                        if ($scoopAvailable -and $scoopAvailable -notmatch "No matches found") {
                            $targetScoopName = $testName
                            break
                        }
                    }
                }
            }

            if ($scoopAvailable -and $scoopAvailable -notmatch "No matches found") {
                Write-Host "  ✓ Found in scoop as '$targetScoopName'!" -ForegroundColor Green

                # Check if already in scoop
                Write-Host "  Checking if $targetScoopName is already in scoop..." -ForegroundColor DarkGray
                $scoopInstalled = scoop list $targetScoopName 2>$null
                if ($scoopInstalled -and $scoopInstalled -match $targetScoopName) {
                    Write-Host "  ✓ $targetScoopName already in scoop, removing from winget only..." -ForegroundColor Gray
                    # Just uninstall from winget since it's already in scoop (use --all-versions)
                    $uninstallResult = Invoke-CommandWithElevation -Command "winget" -Arguments "uninstall $app --accept-source-agreements --silent --all-versions" -Description "Removing $app from winget (already in scoop)"
                    if ($uninstallResult.Success) {
                        Write-Host "  ✓ Removed duplicate from winget" -ForegroundColor Green
                        "$app removed from winget (already in scoop as $targetScoopName)" | Tee-Object -Append $logFile
                    }
                    continue
                }

                # Uninstall from winget first (use --all-versions to handle multiple versions)
                $uninstallResult = Invoke-CommandWithElevation -Command "winget" -Arguments "uninstall $app --accept-source-agreements --silent --all-versions" -Description "Uninstalling $app from winget"

                if ($uninstallResult.Success) {
                    Write-Host "  ✓ WinGet uninstall successful ($($uninstallResult.Method))" -ForegroundColor Green
                    Write-Host "  → Installing $targetScoopName in scoop..." -ForegroundColor Yellow

                    # Install in scoop (try determined name)
                    $scoopResult = scoop install $targetScoopName 2>&1
                    if ($LASTEXITCODE -eq 0) {
                        $wingetMigrated += $app
                        Write-Host "  ✓ SUCCESS: $app migrated from winget to scoop as $targetScoopName!" -ForegroundColor Green
                        "$app successfully migrated from winget to scoop as $targetScoopName" | Tee-Object -Append $logFile
                    } else {
                        Write-Host "  ✗ FAILED: Scoop install failed. Rolling back..." -ForegroundColor Red
                        Write-Host "  Scoop error: $scoopResult" -ForegroundColor DarkRed
                        "Failed to install $app in scoop: $scoopResult" | Tee-Object -Append $logFile
                        # Reinstall in winget if scoop failed
                        Write-Host "  → Reinstalling in winget..." -ForegroundColor Yellow
                        $rollbackResult = Invoke-CommandWithElevation -Command "winget" -Arguments "install $app --accept-source-agreements --silent" -Description "Rolling back $app to winget"
                        if ($rollbackResult.Success) {
                            Write-Host "  ✓ Rollback successful" -ForegroundColor Green
                        } else {
                            Write-Host "  ✗ Rollback failed: $($rollbackResult.Output)" -ForegroundColor Red
                        }
                    }
                } else {
                    Write-Host "  ✗ FAILED: WinGet uninstall failed ($($uninstallResult.Method))" -ForegroundColor Red
                    Write-Host "  WinGet error: $($uninstallResult.Output)" -ForegroundColor DarkRed
                    "Failed to uninstall $app from winget: $($uninstallResult.Output)" | Tee-Object -Append $logFile
                }
            } else {
                $triedNames = if ($namesToTry.Count -gt 0) { " (tried: $($namesToTry -join ', '))" } else { "" }
                Write-Host "  - $app not available in scoop$triedNames, skipping..." -ForegroundColor DarkYellow
            }
        } catch {
            Write-Host "  ✗ ERROR processing ${app}: $_" -ForegroundColor Red
            "Error processing winget app ${app} : $_" | Tee-Object -Append $logFile
        }
    }
} else {
    Write-Host "No WinGet apps found to process." -ForegroundColor Gray
}

$wingetMigrated | Out-File "$tempDir\winget-migrated-to-scoop.txt"

# Final summary
Write-Host "`n" + "="*60 -ForegroundColor Green
Write-Host "MIGRATION COMPLETE!" -ForegroundColor Green
Write-Host "="*60 -ForegroundColor Green
Write-Host "Apps migrated from choco to scoop: $($chocoMigrated.Count)" -ForegroundColor White
if ($chocoMigrated.Count -gt 0) {
    Write-Host "  → $($chocoMigrated -join ', ')" -ForegroundColor Cyan
}
Write-Host "Apps migrated from winget to scoop: $($wingetMigrated.Count)" -ForegroundColor White
if ($wingetMigrated.Count -gt 0) {
    Write-Host "  → $($wingetMigrated -join ', ')" -ForegroundColor Cyan
}
Write-Host "Total new scoop apps: $(($chocoMigrated + $wingetMigrated).Count)" -ForegroundColor Yellow

Write-Host "`nFiles created:" -ForegroundColor Gray
Write-Host "  📄 Log: $logFile"
Write-Host "  📄 Choco apps: $tempDir\choco-apps.txt"
Write-Host "  📄 Choco migrated: $tempDir\choco-migrated-to-scoop.txt"
Write-Host "  📄 Winget migrated: $tempDir\winget-migrated-to-scoop.txt"
if (Test-Path "$tempDir\winget-export.json") {
    Write-Host "  📄 Winget export: $tempDir\winget-export.json"
}

"Migration completed at $(Get-Date)" | Out-File -Append $logFile
Write-Host "`nMigration log saved to: $logFile" -ForegroundColor Green

